mapper.select:
SELECT a.APM_ID as id , a.APM_DATE as date , a.APM_TIME as time , a.APM_NOTE as note , a.CTM_ID as customerId , (SELECT s.STT_NAME FROM `status` s JOIN apm_stt astt ON s.STT_ID = astt.STT_ID WHERE astt.APM_ID = a.APM_ID
AND astt.STT_ID IN #{stt}
ORDER BY astt.ATTIME DESC LIMIT 1 ) AS status FROM appointment a WHERE (SELECT s.STT_NAME FROM `status` s JOIN apm_stt astt ON s.STT_ID = astt.STT_ID WHERE astt.APM_ID = a.APM_ID
AND astt.STT_ID IN #{stt}
ORDER BY astt.ATTIME DESC LIMIT 1 ) IS NOT NULL
AND a.CTM_ID = #{customerId}
AND a.APM_ID = #{id}
AND a.APM_DATE BETWEEN #{dateFrom} AND #{dateTo}
AND a.APM_ID NOT IN ( SELECT astt.APM_ID FROM `status` s JOIN apm_stt astt ON s.STT_ID = astt.STT_ID JOIN appointment a ON a.APM_ID = astt.APM_ID WHERE astt.APM_ID = a.APM_ID AND astt.STT_ID IN #{stt} GROUP BY astt.APM_ID )
ORDER BY a.APM_DATE ASC

SELECT SRV_ID as serviceId
FROM srv_included
WHERE APM_ID = #{id}
