mapper.select:
SELECT ${column} 
FROM ${table}
ORDER BY ${column} DESC
LIMIT 1

SELECT CTM_ID as id ,CTM_NAME as name ,CTM_PHONE as phone ,CTM_CREATEAT as dateCreated ,CTM_ISACTIVE as isActive FROM customer WHERE 1
AND CTM_NAME like #{name}
AND CTM_PHONE like #{phone}

mapper.update:
UPDATE customer
SET CTM_ID = #{id}

        , CTM_NAME = #{name}


        , CTM_PHONE = #{phone}


        , CTM_PASSWORD = #{password}

WHERE CTM_ID = #{id}
