mapper.select:
SELECT 
    STF_ID as id
    ,STF_NAME as name
    ,STF_PHONE as phone
    ,STF_EMAIL as email
    ,STF_PASSWORD as password
    ,STF_ISMANAGER as isManager
    ,STF_JOINEDAT as joinedDate
    ,STF_ISWORKING as isWorking
FROM staff
WHERE 1

    AND STF_NAME like #{name}


    AND STF_PHONE like #{phone}


    AND STF_EMAIL like #{email}


    AND STF_ISMANAGER = #{isManager}


    AND STF_ISWORKING = #{isWorking}
